        </div>

        <div class="form-group">
            <label for="dateRange">Enter a Date Range:</label>
            <input id="dateRange" class="form-control" type="date" placeholder="Select Date Range">
        </div>



            <!-- ... your existing code ... -->

<table class="table">
    <thead>
      <tr>
        <th>Stock Transaction ID</th>
        <th>Stock Creation Date</th>
        <th>Stock Price</th>
      </tr>
    </thead>
    <tbody *ngIf="!isFilterApplied; else filteredTable">
      <!-- Show the original table when no filter is applied -->
      <tr *ngFor="let transaction of stockTransactions">
        <td>{{ transaction.id }}</td>
        <td>{{ transaction.timeStamp }}</td>
        <td>{{ transaction.price }}</td>
      </tr>
    </tbody>
    <ng-template #filteredTable>
      <!-- Show the filtered table when a filter is applied -->
      <tr *ngFor="let transaction of filteredTransactions">
        <td>{{ transaction.id }}</td>
        <td>{{ transaction.timeStamp }}</td>
        <td>{{ transaction.price }}</td>
      </tr>
