<head>
    <meta name="viewport" content="width=device-width, initial-scale=1">
    <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/3.4.1/css/bootstrap.min.css">
    <script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.2/dist/js/bootstrap.bundle.min.js"
            integrity="sha384-C6RzsynM9kWDrMNeT87bh95OGNyZPhcTNXj1NW7RuBCsyN/o0jlpcV8Qyq46cDfL" crossorigin="anonymous"></script>

    <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.7.1/jquery.min.js"></script>
    <script src="https://maxcdn.bootstrapcdn.com/bootstrap/3.4.1/js/bootstrap.min.js"></script>

    <style>

    </style>
</head>
<body>

<div class="container">
    <div class="card">
        <h1>Add Company Details</h1>

                <form [formGroup]="companyForm" (ngSubmit)="submitForm()">
                    <div class="form-group">
                        <input formControlName="companyCode" name="companyCode" class="form-control" type="number" placeholder="Enter Company CODE" required>
                    </div>
                    <div class="form-group">
                        <input formControlName="companyName" name="companyName" class="form-control" type="text" placeholder="Enter Company Name" required>
                    </div>
                    <div class="form-group">
                        <input formControlName="companyCEO" name="companyCEO" class="form-control" type="text" placeholder="Enter companyCEO" required>
                    </div>
                    <div class="form-group">
                        <input formControlName="companyTurnover" name="companyTurnover" class="form-control" type="number" placeholder="Enter companyTurnover" required>
                    </div>
                    <div class="form-group">
                        <input formControlName="companyWebsite" name="companyWebsite" class="form-control" type="text" placeholder="Enter companyWebsite" required>
                    </div>
                    <div class="form-group">
                        <input formControlName="stockExchange" name="stockExchange" class="form-control" type="text" placeholder="Enter stockExchange" required>
                    </div>
                    <!-- <div class="form-group">
                        <input name="stockPrice" class="form-control" type="text" placeholder="Enter stockPrice" required>
                    </div> -->
                    <button class="btn btn-success" type="submit" >Add Details</button>
                </form><!-- Add other form fields as needed -->

    </div>

    <div class="card">
        <h1>View All Companies</h1>
        <button class="btn btn-info loginbtn" (click)="getAllCompanies()">Get All Company</button>
        <table class="table table-striped">
            <table class="table table-striped">
                <thead>
                <tr>
                    <th>COMPANY CODE</th>
                    <th>COMPANY NAME</th>
                    <th>CEO</th>
                    <th>TURNOVER</th>
                    <th>WEBSITE</th>
                    <th>STOCK EXCHANGE</th>
                    <th>STOCK PRICE</th>
                    <th>ACTION</th>
                </tr>
                </thead>
                <tbody>
                <tr *ngFor="let c of companies">
                    <td>{{c.companyCode}}</td>
                    <td>{{ c.companyName }}</td>
                    <td>{{ c.companyCEO }}</td>
                    <td>{{ c.companyTurnover}}</td>
                    <td>{{ c.companyWebsite }}</td>
                    <td>{{ c.stockExchange }}</td>
                    <td>{{ c.latestStockPrice }}</td>

                    <td>
                        <button class="btn btn-warning" >Update</button>
                        <button class="btn btn-danger" >Delete</button>
                    </td>
                </tr>
                </tbody>
            </table>
        </table>
    </div>

    <div class="card">
        <h1>Search Company</h1>
            <div class="input-group mb-3">

                <div class="input-group-append align-items-center">
                    <input type="text" [(ngModel)]="searchText" class="form-control fa-search" placeholder="Search" >
                    <!-- <span class="input-group-text"><i class="fa fa-search"></i></span> -->
                </div>
            </div>

                <table  class="table table-striped">
                    <thead>
                    <tr>
                        <th>COMPANY CODE</th>
                        <th>COMPANY NAME</th>
                        <th>CEO</th>
                        <th>COMPANY TURNOVER</th>
                        <th>COMPANY WEBSITE</th>
                        <th>STOCK EXCHANGE</th>
                        <th>STOCK PRICE</th>
                    </tr>
                    </thead>
                    <tbody  *ngFor="let r of companies | filter:searchText">
                    <tr >
                        <td>{{ r.companyCode }}</td>
