import { Component, OnInit } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MatDialog } from '@angular/material/dialog';
import Swal from 'sweetalert2';
import { Company } from '../model/company';

@Component({
  selector: 'app-company',
  templateUrl: './company.component.html',
  styleUrls: ['./company.component.css']
})
export class CompanyComponent implements OnInit {

  companyForm!: FormGroup;
  companies: Company[] = [];
  searchText: string = '';
  selectedCompany: Company | null = null;
  isUpdate = false;

  constructor(
    private http: HttpClient,
    private formBuilder: FormBuilder,
    private dialog: MatDialog
  ) { }

  ngOnInit() {
    this.companyForm = this.formBuilder.group({
      companyCode: ['', Validators.required],
      companyName: ['', Validators.required],
      companyCEO: ['', Validators.required],
      companyTurnover: ['', Validators.required],
      companyWebsite: ['', Validators.required],
      stockExchange: ['', Validators.required],
    });

    this.getAllCompanies();
  }

  updateForm() {
    if (this.selectedCompany != null) {
      if (this.companyForm.invalid) {
        console.log("invalid");
        return;
      }

      const url = `http://localhost:8081/v1.0/market/company/put/${this.selectedCompany.companyCode}`;
      const token = sessionStorage.getItem('token');
      const body = {
        companyName: this.selectedCompany.companyName,
        companyCEO: this.selectedCompany.companyCEO,
        companyTurnover: this.selectedCompany.companyTurnover,
        companyWebsite: this.selectedCompany.companyWebsite,
        stockExchange: this.selectedCompany.stockExchange
      };

      const httpOptions = {
        headers: new HttpHeaders({
          'Content-Type': 'application/json',
          'Authorization': 'Bearer ' + token
        })
      };

      this.http.put(url, body, httpOptions).subscribe(
        (response) => {
          console.log(response);
          console.log('Company updated successfully');
          this.getAllCompanies();
          Swal.fire({
            icon: 'success',
            title: 'Success',
            text: 'Company updated successfully!',
          });
        },
        (error) => {
          console.error(error);
          // Handle error response here
        }
      );
    }
  }

  submitForm() {
    if (this.companyForm.invalid) {
      return;
    }

    const url = 'http://localhost:8081/v1.0/market/company/register';
    const token = sessionStorage.getItem('token');
    const body = {
      companyName: this.companyForm.value.companyName,
      companyCEO: this.companyForm.value.companyCEO,
      companyTurnover: this.companyForm.value.companyTurnover,
      companyWebsite: this.companyForm.value.companyWebsite,
      stockExchange: this.companyForm.value.stockExchange,
    };

    const httpOptions = {
      headers: new HttpHeaders({
        'Content-Type': 'application/json',
        'Authorization': 'Bearer ' + token
      })
    };

    this.http.post(url, body, httpOptions).subscribe(
      () => {
        console.log('Company added successfully');
        this.getAllCompanies();
        Swal.fire({
          icon: 'success',
          title: 'Success',
          text: 'Company added successfully!',
        });
      },
      (error) => {
        console.error(error);
        // Handle error response here
      }
    );
  }

  getAllCompanies() {
    const url = 'http://localhost:8081/v1.0/market/company/getAll';
    const token = sessionStorage.getItem('token');

    const httpOptions = {
      headers: new HttpHeaders({
        'Content-Type': 'application/json',
        'Authorization': 'Bearer ' + token
      })
    };

    this.http.get<Company[]>(url, httpOptions).subscribe(companies => {
      this.companies = companies.map(company => ({
        ...company,
        editMode: false
      }));
    });
  }

  update(c: Company) {
    const url = `http://localhost:8081/v1.0/market/company/put/${c.companyCode}`;
    const httpOptions = {
      headers: new HttpHeaders({
        'Content-Type': 'application/json',
        'Authorization': 'Bearer ' + sessionStorage.getItem('token')
      })
    };

    this.selectedCompany = c;
    const body = {
      companyName: c.companyName,
      companyCEO: c.companyCEO,
      companyTurnover: c.companyTurnover,
      companyWebsite: c.companyWebsite,
      stockExchange: c.stockExchange
    };

    this.http.put(url, body, httpOptions).subscribe(
      (response) => {
        console.log(response);
        console.log('Company updated successfully');
        this.getAllCompanies();
      },
      (error) => {
        console.error(error);
        // Handle error response here
      }
    );
  }

  deleteCompany(companyCode: number): void {
    const url = `http://localhost:8081/v1.0/market/company/delete/${companyCode}`;
    const httpOptions = {
      headers: new HttpHeaders({
        'Content-Type': 'application/json',
        'Authorization': 'Bearer ' + sessionStorage.getItem('token')
      }),
      responseType: 'text' as 'json'
    };

    this.http.delete(url, httpOptions).subscribe(
      () => {
        console.log("Reloading admin dashboard after delete!!");
        this.ngOnInit();
      },
      (error) => {
        console.error(error);
      }
    );
  }
}

