import { Component } from '@angular/core';

@Component({
  selector: 'app-stock',
  templateUrl: './stock.component.html',
  styleUrls: ['./stock.component.css']
})
export class StockComponent {

}
<!DOCTYPE html>
<html lang="en">
<head>
    <meta name="viewport" content="width=device-width, initial-scale=1">
    <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/3.4.1/css/bootstrap.min.css">
    <script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.2/dist/js/bootstrap.bundle.min.js"
            integrity="sha384-C6RzsynM9kWDrMNeT87bh95OGNyZPhcTNXj1NW7RuBCsyN/o0jlpcV8Qyq46cDfL" crossorigin="anonymous"></script>

    <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.7.1/jquery.min.js"></script>
    <script src="https://maxcdn.bootstrapcdn.com/bootstrap/3.4.1/js/bootstrap.min.js"></script>

    <style>
        body {
            margin: 0;
            padding: 0;
            background-image: url('https://img.freepik.com/free-vector/gradient-stock-market-concept_23-2149166910.jpg');
            background-size: cover;
            background-position: center;
            font-family: 'Arial', sans-serif;
            color: rgb(253, 250, 250);
        }

        .container {
            display: flex;
            justify-content: center;
            align-items: center;
            height: 100vh;
            flex-wrap: wrap;
        }

        .card {
            border-radius: 15px;
            border-left: 5px solid #04AA6D;
            box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
            margin-bottom: 20px;
            background-color: rgb(85, 167, 170);
            display: flex;
            flex-direction: column;
            align-items: center;
            padding: 20px;
            width: 80%; /* Adjust the width of the card */
        }

        .card:hover {
            transform: scale(1.02);
        }

        h1 {
            margin-top: 10px;
        }

        .loginbtn {
            background-color: #04AA6D;
            color: white;
            border: none;
            cursor: pointer;
            opacity: 0.9;
            transition: opacity 0.3s ease-in-out;
            margin-top: 10px;
            padding: 10px 20px;
        }

        .loginbtn:hover {
            opacity: 1;
        }
    </style>
</head>
<body>

<div class="container">
    <div class="card">
        <h1>Stock Information Form</h1>
        <form>
            <div class="form-group">
                <input name="stockPrice" class="form-control" type="text" placeholder="Stock Price" required>
            </div>
            <div class="form-group">
                <input name="stockCreationDate" class="form-control" type="date" placeholder="Stock Creation Date" required>
            </div>
            <div class="form-group">
                <input name="companyCode" class="form-control" type="number" placeholder="Company Code" required>
            </div>
            <button class="btn btn-success loginbtn" type="submit">Submit</button>
        </form>
    </div>
</div>

</body>
</html>
