import { Component } from '@angular/core';

@Component({
  selector: 'app-company',
  templateUrl: './company.component.html',
  styleUrls: ['./company.component.css']
})
export class CompanyComponent {

}
<!DOCTYPE html>
<html lang="en">
<head>
    <meta name="viewport" content="width=device-width, initial-scale=1">
    <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/3.4.1/css/bootstrap.min.css">
    <style>
        body {
            margin: 0;
            padding: 0;
            background-image: url('https://img.freepik.com/free-vector/gradient-stock-market-concept_23-2149166910.jpg');
            background-size: cover;
            background-position: center;
            font-family: 'Arial', sans-serif;
            color: rgb(253, 250, 250);
        }

        .container {
            display: flex;
            justify-content: center;
            align-items: center;
            height: 100vh;
            flex-wrap: wrap;
            margin: 20px; /* Adjust the margin for spacing between containers */
        }

        .column {
            flex: 1;
            padding: 20px;
            text-align: center;
        }

        .card {
            border-radius: 15px;
            border-left: 5px solid #04AA6D;
            box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
            margin-bottom: 20px;
            background-color: rgb(85, 167, 170);
            display: flex;
            flex-direction: column;
            align-items: center;
        }

        .card:hover {
            transform: scale(1.02);
        }

        .card-image {
            width: 100%;
            max-height: 150px;
            object-fit: cover;
            border-top-left-radius: 15px;
            border-top-right-radius: 15px;
        }

        h2 {
            margin-top: 10px;
        }

        .loginbtn {
            background-color: #04AA6D;
            color: white;
            border: none;
            cursor: pointer;
            opacity: 0.9;
            transition: opacity 0.3s ease-in-out;
            margin-top: 10px;
            padding: 10px 20px;
        }

        .loginbtn:hover {
            opacity: 1;
        }

        h1 {
            text-align: center;
            margin-bottom: 30px;
        }
    </style>
    <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.7.1/jquery.min.js"></script>
    <script src="https://maxcdn.bootstrapcdn.com/bootstrap/3.4.1/js/bootstrap.min.js"></script>
</head>
<body>

<div class="container">
    <div class="column">
        <div class="card">
            <h2>Company Management</h2>
            <img src="your-company-image-url.jpg" alt="Company Management Image" class="card-image">
            <button class="loginbtn">Go to Company Management Console</button>
        </div>
    </div>
</div>

<div class="container">
    <div class="column">
        <div class="card">
            <h2>Stock Management</h2>
            <img src="your-stock-image-url.jpg" alt="Stock Management Image" class="card-image">
            <button class="loginbtn">Go to Stock Management Console</button>
        </div>
    </div>
</div>

</body>
</html>
