import { Component, OnInit } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { LoginResponse } from '../model/loginresponse';
import { AuthService } from '../service/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {
  loginForm!: FormGroup;
  isError = false;

  constructor(private authService: AuthService, private router: Router) {}

  ngOnInit(): void {
    this.loginForm = new FormGroup({
      'username': new FormControl(null, Validators.required),
      'password': new FormControl(null, Validators.required),
    });
  }

  onSubmit(): void {
    console.log(this.loginForm.value);
    this.login(this.loginForm.value);
  }

  logout(): void {
    // clear specific session storage items
    sessionStorage.removeItem('user');
    sessionStorage.removeItem('token');
  }


  login(userData: any): void {
    this.authService.login(userData.username, userData.password).subscribe(
      (response: LoginResponse) => {
        console.log(response);

        //making sure the user is loggedout
        this.logout();
        // handle your response here
        sessionStorage.setItem('user', JSON.stringify(response.user));
        sessionStorage.setItem('token', response.token);

        console.log("Logging Session Item -> ");

        let userItem = sessionStorage.getItem('user');
        let jwtTokenItem = sessionStorage.getItem('token');

        if (userItem !== null) {
          let user = JSON.parse(userItem);
          console.log("username -> ", user.username);
          console.log("password -> ", user.password);

          if (user.role === 'admin') {
            this.router.navigate(['/company']);
          }else if(user.role === 'user'){
            this.router.navigate(['/dasboard'])
          }

        } else {
          console.error("User not found in sessionStorage");
        }

        if (jwtTokenItem !== null) {
          let jwtToken = jwtTokenItem;
          console.log("token -> ", token);
        } else {
          console.error("JwtToken not found in sessionStorage");
        }
      },
      error => {
        console.error(error);
        this.isError = true;
      }
    );
  }

}

function token(arg0: string, token: any) {
  throw new Error('Function not implemented.');
}
<div class="container">
    <div class="row justify-content-center">
        <div class="col-md-6">
            <div class="card shadow mt-5">
                <div class="card-body">
                    <h1 class="text-center mb-4">Login</h1>
                    <p class="text-center">Please fill in this form to login to your account.</p>
                    <hr>
                    <form [formGroup]="loginForm" (ngSubmit)="onSubmit()">
                        <div class="form-group">
                            <label for="username"><b>Username</b></label>
                            <input type="text" formControlName="username" placeholder="Enter Username" name="username" id="username" class="form-control" required>
                        </div>

                        <div class="form-group">
                            <label for="password"><b>Password</b></label>
                            <input type="password" formControlName="password" placeholder="Enter Password" name="password" id="password" class="form-control" required>
                        </div>

                        <button type="submit" class="btn btn-block loginbtn">Login</button>

                        <div class="text-center mt-4">
                            <p>Don't have an account? <a routerLink="/register">Sign Up</a>.</p>
                            <!-- <p> Forgot <a routerLink="/forgot-password"> password </a>? or Forgot <a routerLink="/forgot-username"> UserName</a>?</p> -->
                        </div>
                    </form>
                </div>
            </div>
        </div>
    </div>
</div>

