import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Company } from '../model/company';
import { EstockService } from '../service/EStock.service';

@Component({
  selector: 'app-company-info',
  templateUrl: './company-info.component.html',
  styleUrls: ['./company-info.component.css']
})
export class CompanyInfoComponent implements OnInit {
  companyCode!: number;
  companyDetails!: Company;

  constructor(private route: ActivatedRoute, private http: HttpClient, private es: EstockService) { }

  ngOnInit(): void {
    this.route.params.subscribe(params => {
      this.companyCode = +params['companyCode'];
      this.getCompanyDetails();
    });
  }

  getCompanyDetails(): void {
    this.es.getCompanyDetailsByCode(this.companyCode).subscribe(
      (data: Company) => {
        this.companyDetails = data;
      },
      (error) => {
        console.error(error);
        // Handle error response here
      }
    );
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
    <meta name="viewport" content="width=device-width, initial-scale=1">
    <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/3.4.1/css/bootstrap.min.css">
    <script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.2/dist/js/bootstrap.bundle.min.js"
            integrity="sha384-C6RzsynM9kWDrMNeT87bh95OGNyZPhcTNXj1NW7RuBCsyN/o0jlpcV8Qyq46cDfL" crossorigin="anonymous"></script>

    <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.7.1/jquery.min.js"></script>
    <script src="https://maxcdn.bootstrapcdn.com/bootstrap/3.4.1/js/bootstrap.min.js"></script>

    <style>
        body {
            margin: 0;
            padding: 0;
            background-image: url('https://img.freepik.com/free-vector/gradient-stock-market-concept_23-2149166910.jpg');
            background-size: cover;
            background-position: center;
            font-family: 'Arial', sans-serif;
            color: rgb(253, 250, 250);
        }

        .container {
            display: flex;
            justify-content: space-around;
            align-items: flex-start;
            height: 100vh;
            flex-wrap: wrap;
        }

        .card {
            border-radius: 15px;
            border-left: 5px solid #04AA6D;
            box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
            margin-bottom: 20px;
            background-color: rgb(85, 167, 170);
            display: flex;
            flex-direction: column;
            align-items: center;
            padding: 20px;
            width: 45%; /* Adjust the width of the card */
        }

        .card:hover {
            transform: scale(1.02);
        }

        h1 {
            margin-top: 10px;
        }

        .loginbtn {
            background-color: #04AA6D;
            color: white;
            border: none;
            cursor: pointer;
            opacity: 0.9;
            transition: opacity 0.3s ease-in-out;
            margin-top: 10px;
            padding: 10px 20px;
        }

        .loginbtn:hover {
            opacity: 1;
        }

        .form-group {
            margin-bottom: 1.5rem;
        }

        .form-control {
            border-radius: 0.25rem;
            border: 1px solid #ced4da;
            padding: 0.375rem 0.75rem;
            transition: border-color 0.15s ease-in-out, box-shadow 0.15s ease-in-out;
        }

        .form-control:focus {
            color: #495057;
            background-color: #fff;
            border-color: #80bdff;
            outline: 0;
            box-shadow: 0 0 0 0.2rem rgba(0, 123, 255, 0.25);
        }

        table {
            width: 100%;
            border-collapse: collapse;
            margin-top: 20px;
        }

        th, td {
            border: 1px solid #ddd;
            padding: 8px;
            text-align: left;
        }

        th {
            background-color: #04AA6D;
            color: white;
        }

        .pagination {
            display: flex;
            justify-content: center;
            margin-top: 10px;
        }

        .pagination button {
            background-color: #04AA6D;
            color: white;
            border: none;
            padding: 8px 16px;
            margin: 0 4px;
            cursor: pointer;
        }

        .pagination button:hover {
            background-color: #027c50;
        }
    </style>
</head>
<body>

<div class="container">
    <div class="card">
        <h1>Company Details</h1>
        <div class="form-group">
            <label for="companyCode">Company Code:</label>
            <div>{{ companyDetails?.companyCode }}</div>
        </div>
        <div class="form-group">
            <label for="companyName">Company Name:</label>
            <div>{{ companyDetails?.companyName }}</div>
          </div>
          <div class="form-group">
            <label for="companyTurnover">Company Turnover:</label>
            <div>{{ companyDetails?.companyTurnover }}</div>
          </div>
          <div class="form-group">
            <label for="companyWebsite">Company Website:</label>
            <div>{{ companyDetails?.companyWebsite }}</div>
          </div>
          <div class="form-group">
            <label for="stockExchange">Company Stock Exchange:</label>
            <div>{{ companyDetails?.stockExchange }}</div>
          </div>
          <div class="form-group">
            <label for="latestStockPrice">Latest Stock Price:</label>
            <div>{{ companyDetails?.latestStockPrice }}</div>
          </div>
    </div>

    <div class="card">
        <h1>Stock Transactions</h1>
        <div class="form-group">
            <label for="filter">Filter:</label>
            <input id="filter" class="form-control" type="text" placeholder="Enter Filter">
        </div>
        <div class="form-group">
            <label for="dateRange">Enter a Date Range:</label>
            <input id="dateRange" class="form-control" type="date" placeholder="Select Date Range">
        </div>

        <table class="table table-striped">
            <thead>
            <tr>
                <th>Stock Transaction ID</th>
                <th>Stock Creation Date</th>
                <th>Stock Price</th>
            </tr>
            </thead>
            <tbody>
            <!-- Rows will be dynamically populated based on data -->
            </tbody>
        </table>

        <div class="pagination">
            <button>1</button>
            <button>2</button>
            <!-- Add more buttons as needed -->
        </div>
    </div>
</div>

</body>
</html>
