    <style>
        body {
            margin: 0;
            padding: 0;
            background-image: url('https://img.freepik.com/free-vector/gradient-stock-market-concept_23-2149166910.jpg');
            background-size: cover;
            background-position: center;
            font-family: 'Arial', sans-serif;
            color: rgb(70, 17, 17);
        }

        .container {
            display: flex;
            justify-content: center;
            align-items: center;
            height: 100vh;
            flex-wrap: wrap;
        }

        .card {
            border-radius: 15px;
            border-left: 5px solid #04AA6D;
            box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
            margin-bottom: 20px;
            background-color: rgb(85, 167, 170);
            display: flex;
            flex-direction: column;
            align-items: center;
            padding: 20px;
            width: 80%; /* Adjust the width of the card */
        }

        .card:hover {
            transform: scale(1.02);
        }

        h1 {
            margin-top: 10px;
        }

        .loginbtn {
            background-color: #04AA6D;
            color: white;
            border: none;
            cursor: pointer;
            opacity: 0.9;
            transition: opacity 0.3s ease-in-out;
            margin-top: 10px;
            padding: 10px 20px;
        }

        .loginbtn:hover {
            opacity: 1;
        }
    </style>
</head>
