<div class="container">
    <div class="card">
        <h1>Stock Information Form</h1>
        <form [formGroup]="companyForm" (ngSubmit)="submitForm()">
            <div class="form-group">
                <input name="companyCode" class="form-control" type="number" placeholder="Company Code" required>
            </div>
            <div class="form-group">
                <input formControlName="stockPrice" class="form-control" type="text" placeholder="Stock Price" required>
            </div>
            <!-- Add other form controls here -->
            <button class="btn btn-success loginbtn" type="submit">Submit</button>
        </form>
    </div>
</div>

