import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { Observable } from 'rxjs';
import { Company } from '../model/company';
import { AuthService } from '../service/auth.service';
import { EstockService } from '../service/EStock.service';
import Swal from 'sweetalert2';
import { MatDialog } from '@angular/material/dialog';
import { UpdateFormComponent } from '../update-form/update-form.component';
@Component({
  selector: 'app-company',
  templateUrl: './company.component.html',
  styleUrls: ['./company.component.css']
})
export class CompanyComponent {


  companyForm!: FormGroup;
  companies: Company[] = [];
  searchText: string = '';
   selectedCompany: Company | null = null;
  isUpdate = false;
  constructor(private authService: AuthService, private http: HttpClient, private router: Router,private formBuilder: FormBuilder, private dialog: MatDialog) { }

  ngOnInit() {
    this.companyForm = this.formBuilder.group({
      companyCode: ['', Validators.required],
      companyName: ['', Validators.required],
      companyCEO: ['', Validators.required],
      companyTurnover: ['', Validators.required],
      companyWebsite: ['', Validators.required],
      stockExchange: ['', Validators.required],
      //stockPrice: ['', Validators.required],
      //latestStockPrice: ['', Validators.required],

    });
    this.getAllCompanies();
    //this.getMov();


  }
  // updateCompany(c: Company) {
  //   this.isUpdate=true;

  //   console.log("insde");
  //   const dialogRef = this.dialog.open(UpdateFormComponent, {
  //     width: '400px',
  //     data: c
  //   });

  //   dialogRef.afterClosed().subscribe(result => {
  //     if (result) {
  //       const updatedCompany: Company = {
  //         companyCode: c.companyCode, // Make sure to include the companyCode in the updated data
  //         ...result // Spread the updated data
  //       };

  //       const url = `http://localhost:8081/v1.0/market/company/put/${updatedCompany.companyCode}`;
  //       const httpOptions = {
  //         headers: new HttpHeaders({
  //           'Content-Type': 'application/json',
  //           'Authorization': 'Bearer ' + sessionStorage.getItem('token')
  //         })
  //       };

  //       this.http.put(url, updatedCompany, httpOptions).subscribe(
  //         () => {
  //           console.log('Company updated successfully');
  //           this.getAllCompanies(); // Refresh the company list
  //         },
  //         (error) => {
  //           console.error(error);
  //           // Handle error response here
  //         }
  //       );
  //     }
  //   });
  // }
  submitForm() {
    if (this.companyForm.invalid) {
      return;

    }
    console.log("inside")
    const url = 'http://localhost:8081/v1.0/market/company/register';
    const token = sessionStorage.getItem('token');
    const body = {
      companyCode: this.companyForm.value.companyCode,
      companyName: this.companyForm.value.companyName,
      companyCEO: this.companyForm.value.companyCEO,
      companyTurnover: this.companyForm.value.companyTurnover,
      companyWebsite: this.companyForm.value.companyWebsite,
      stockExchange: this.companyForm.value.stockExchange,
      //stockPrice: this.companyForm.value.stockPrice,
     // latestStockPrice: this.companyForm.value.latestStockPrice,

    };
    console.log(this.companyForm) 
    const httpOptions = {
      headers: new HttpHeaders({
        'Content-Type': 'application/json',
        'Authorization': 'Bearer ' + token
      })
    };

    console.log('Company Name:', this.companyForm.value.companyName);

    this.http.post(url, body, httpOptions).subscribe(
      () => {
        console.log('Company added successfully');
        // Handle success response here
        this.getAllCompanies();

      },
      (error) => {
        console.error(error);
        // Handle error response here
      }
    );
    Swal.fire({
      icon: 'success',
      title: 'Success',
      text: 'Company added successfully!',
    });

  }
getAllCompanies() {
  const url = 'http://localhost:8081/v1.0/market/company/getAll';
  const token = sessionStorage.getItem('token');

  console.log("Token -> ", token);

  const httpOptions = {
    headers: new HttpHeaders({
      'Content-Type':  'application/json',
      'Authorization': 'Bearer ' + token
    })
  };
  console.log("httpOptions -> ", httpOptions);
  this.http.get<Company[]>(url, httpOptions).subscribe(movies => {
    this.companies = movies.map(movie => ({
      ...movie,
      editMode: false
    }));
    const latestStockPrices = this.companies.map(company => company.latestStockPrice);
        console.log('Latest Stock Prices:', latestStockPrices);
  });
}
update(c: Company) {
  const url = `http://localhost:8081/v1.0/market/company/put/${c.companyCode}`;
  this.isUpdate=true;
  console.log("Inside");
  const httpOptions = {
    headers: new HttpHeaders({
      'Content-Type': 'application/json',
      'Authorization': 'Bearer ' + sessionStorage.getItem('token')
    })
  };
  this.selectedCompany=c;
  const body = {
    companyName: c.companyName,
    companyCEO: c.companyCEO,
    companyTurnover: c.companyTurnover,
    companyWebsite: c.companyWebsite,
    stockExchange: c.stockExchange
  };


  this.http.put(url, body, httpOptions).subscribe(
    (response) => {
      console.log(response)
      console.log('Company updated successfully');
      return this.getAllCompanies;
      // Handle success response here
    },
    (error) => {
      console.error(error);
      // Handle error response here
    }
  );

}


deleteCompany(companyCode: number): void {
  const url = `http://localhost:8081/v1.0/market/company/delete/${companyCode}`;
  const httpOptions = {
    headers: new HttpHeaders({
      'Content-Type': 'application/json',
      'Authorization': 'Bearer ' + sessionStorage.getItem('token')
    }),
    responseType: 'text' as 'json' // here
  };
  this.http.delete(url, httpOptions).subscribe(
    () => {
      console.log("Reloading admin dashboard after delete!!");
      this.ngOnInit();
    },
    (error) => {
      console.error(error);
    }
  );
}
}
<!DOCTYPE html>
<html lang="en">
<head>
    <meta name="viewport" content="width=device-width, initial-scale=1">
    <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/3.4.1/css/bootstrap.min.css">
    <script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.2/dist/js/bootstrap.bundle.min.js"
            integrity="sha384-C6RzsynM9kWDrMNeT87bh95OGNyZPhcTNXj1NW7RuBCsyN/o0jlpcV8Qyq46cDfL" crossorigin="anonymous"></script>

    <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.7.1/jquery.min.js"></script>
    <script src="https://maxcdn.bootstrapcdn.com/bootstrap/3.4.1/js/bootstrap.min.js"></script>

    <style>

    </style>
</head>
<body>

<div class="container">
    <div class="card" *ngIf="!isUpdate">
        <h1>Add Company Details</h1>

                <form [formGroup]="companyForm" (ngSubmit)="submitForm()">
                    <div class="form-group">
                        <input formControlName="companyCode" name="companyCode" class="form-control" type="number" placeholder="Enter Company CODE" required>
                    </div>
                    <div class="form-group">
                        <input formControlName="companyName" name="companyName" class="form-control" type="text" placeholder="Enter Company Name" required>
                    </div>
                    <div class="form-group">
                        <input formControlName="companyCEO" name="companyCEO" class="form-control" type="text" placeholder="Enter companyCEO" required>
                    </div>
                    <div class="form-group">
                        <input formControlName="companyTurnover" name="companyTurnover" class="form-control" type="number" placeholder="Enter companyTurnover" required>
                    </div>
                    <div class="form-group">
                        <input formControlName="companyWebsite" name="companyWebsite" class="form-control" type="text" placeholder="Enter companyWebsite" required>
                    </div>
                    <div class="form-group">
                        <input formControlName="stockExchange" name="stockExchange" class="form-control" type="text" placeholder="Enter stockExchange" required>
                    </div>
                    <!-- <div class="form-group">
                        <input name="stockPrice" class="form-control" type="text" placeholder="Enter stockPrice" required>
                    </div> -->
                    <button class="btn btn-success" type="submit" >Add Details</button>
                </form><!-- Add other form fields as needed -->

    </div>
    <div class="card" *ngIf="isUpdate && selectedCompany!=null">
        <h1>Update Company Details</h1>

                <form [formGroup]="companyForm" (ngSubmit)="submitForm()">
                    <div class="form-group">
                        <input formControlName="companyCode" name="companyCode" class="form-control" type="number" placeholder="Enter Company CODE"  [disabled]="isUpdate">
                    </div>
                    <div class="form-group">
                        <input formControlName="companyName" name="companyName" class="form-control" type="text" placeholder="Enter Company Name" [disabled]="isUpdate">
                    </div>
                    <div class="form-group">
                        <input formControlName="companyCEO" [value]="selectedCompany?.companyCEO" name="companyCEO" class="form-control" type="text" placeholder="Enter companyCEO" required>
                    </div>
                    <div class="form-group">
                        <input formControlName="companyTurnover" name="companyTurnover" class="form-control" type="number" placeholder="Enter companyTurnover" required>
                    </div>
                    <div class="form-group">
                        <input formControlName="companyWebsite" name="companyWebsite" class="form-control" type="text" placeholder="Enter companyWebsite" required>
                    </div>
                    <div class="form-group">
                        <input formControlName="stockExchange" name="stockExchange" class="form-control" type="text" placeholder="Enter stockExchange" required>
                    </div>
                    <!-- <div class="form-group">
                        <input name="stockPrice" class="form-control" type="text" placeholder="Enter stockPrice" required>
                    </div> -->
                    <button class="btn btn-warning" type="submit" >Update Details</button>
                </form><!-- Add other form fields as needed -->

    </div>
    <div class="card">
        <h1>View All Companies</h1>
        <button class="btn btn-info loginbtn" (click)="getAllCompanies()">Get All Company</button>
        <div class="input-group mb-3">

            <div class="input-group-append align-items-center">
                <input type="text" [(ngModel)]="searchText"  class="form-control fa-search" placeholder="Search" >
                <!-- <span class="input-group-text"><i class="fa fa-search"></i></span> -->
            </div>
        </div>
        <table class="table table-striped">
            <table class="table table-striped">
                <thead>
                <tr>
                    <th>COMPANY CODE</th>
                    <th>COMPANY NAME</th>
                    <th>CEO</th>
                    <th>TURNOVER</th>
                    <th>WEBSITE</th>
                    <th>STOCK EXCHANGE</th>
                    <th>LATEST STOCK PRICE</th>
                    <th>ACTION</th>
                </tr>
                </thead>
                <tbody>
                <tr *ngFor="let c of companies | filter:searchText">
                    <td>{{ c.companyCode }}</td>
                    <td>{{ c.companyName }}</td>
                    <td>{{ c.companyCEO }}</td>
                    <td>{{ c.companyTurnover }}</td>
                    <td>{{ c.companyWebsite }}</td>
                    <td>{{ c.stockExchange }}</td>
                    <td>{{ c.latestStockPrice.price }}</td>

                    <td>
                        <button class="btn btn-danger" (click)="deleteCompany(c.companyCode)">Delete</button>
                        <button class="btn btn-warning" (click)="update(c)">Update</button>
                    </td>
                </tr>
                </tbody>
            </table>
        </table>
    </div>


    <div class="card">
        <h1>Search Company</h1>
            <div class="input-group mb-3">

                <div class="input-group-append align-items-center">
                    <input type="text" [(ngModel)]="searchText"  class="form-control fa-search" placeholder="Search" >
                    <!-- <span class="input-group-text"><i class="fa fa-search"></i></span> -->
                </div>
            </div>

                <table  class="table table-striped">
                    <thead>
                    <tr>
                        <th>COMPANY CODE</th>
                        <th>COMPANY NAME</th>
                        <th>CEO</th>
                        <th>COMPANY TURNOVER</th>
                        <th>COMPANY WEBSITE</th>
                        <th>STOCK EXCHANGE</th>
                        <th>LATEST STOCK PRICE</th>
                    </tr>
                    </thead>
                    <tbody  *ngFor="let r of companies | filter:searchText">
                    <tr >
                        <td>{{ r.companyCode }}</td>
                        <td>{{ r.companyName }}</td>
                        <td>{{ r.companyCEO }}</td>
                        <td>{{ r.companyTurnover}}</td>
                        <td>{{ r.companyWebsite }}</td>
                        <td>{{ r.stockExchange }}</td>
                        <td>{{ r.latestStockPrice.price }}</td>
                    </tr>
                    </tbody>
                </table>
    </div>
</div>

</body>
</html>
