import { Component } from '@angular/core';

@Component({
  selector: 'app-company',
  templateUrl: './company.component.html',
  styleUrls: ['./company.component.css']
})
export class CompanyComponent {

}
<!DOCTYPE html>
<html lang="en">
<head>
    <meta name="viewport" content="width=device-width, initial-scale=1">
    <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/3.4.1/css/bootstrap.min.css">
    <script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.2/dist/js/bootstrap.bundle.min.js"
            integrity="sha384-C6RzsynM9kWDrMNeT87bh95OGNyZPhcTNXj1NW7RuBCsyN/o0jlpcV8Qyq46cDfL" crossorigin="anonymous"></script>

    <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.7.1/jquery.min.js"></script>
    <script src="https://maxcdn.bootstrapcdn.com/bootstrap/3.4.1/js/bootstrap.min.js"></script>

    <style>
        body {
            margin: 0;
            padding: 0;
            background-image: url('https://img.freepik.com/free-vector/gradient-stock-market-concept_23-2149166910.jpg');
            background-size: cover;
            background-position: center;
            font-family: 'Arial', sans-serif;
            color: rgb(253, 250, 250);
        }

        .container {
            display: flex;
            justify-content: center;
            align-items: center;
            height: 100vh;
            flex-wrap: wrap;
        }

        .row {
            justify-content: space-around;
            width: 100%;
        }

        .col-md-4 {
            flex: 0 0 30%; /* Adjust the column width as needed */
        }

        .card {
            border-radius: 15px;
            border-left: 5px solid #04AA6D;
            box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
            margin-bottom: 20px;
            background-color: rgb(85, 167, 170);
            display: flex;
            flex-direction: column;
            align-items: center;
            padding: 20px;
        }

        .card:hover {
            transform: scale(1.02);
        }

        h1 {
            margin-top: 10px;
        }

        .loginbtn {
            background-color: #04AA6D;
            color: white;
            border: none;
            cursor: pointer;
            opacity: 0.9;
            transition: opacity 0.3s ease-in-out;
            margin-top: 10px;
            padding: 10px 20px;
        }

        .loginbtn:hover {
            opacity: 1;
        }
    </style>
</head>
<body>

<div class="container">
    <div class="row">
        <div class="col-md-4">
            <div class="card">
                <h1>Add Company Details</h1>
                <form>
                    <div class="form-group">
                        <input name="companyCode" class="form-control" type="number" placeholder="Enter Company CODE" required>
                    </div>
                    <form>
                        <div class="form-group">
                            <input name="companyCode" class="form-control" type="number" placeholder="Enter Company CODE" required>
                        </div>
                        <div class="form-group">
                            <input name="companyName" class="form-control" type="text" placeholder="Enter Company Name" required>
                        </div>
                        <div class="form-group">
                            <input name="companyCEO" class="form-control" type="text" placeholder="Enter companyCEO" required>
                        </div>
                        <div class="form-group">
                            <input name="companyTurnover" class="form-control" type="number" placeholder="Enter companyTurnover" required>
                        </div>
                        <div class="form-group">
                            <input name="companyWebsite" class="form-control" type="text" placeholder="Enter companyWebsite" required>
                        </div>
                        <div class="form-group">
                            <input name="stockExchange" class="form-control" type="text" placeholder="Enter stockExchange" required>
                        </div>
                        <div class="form-group">
                            <input name="stockPrice" class="form-control" type="text" placeholder="Enter stockPrice" required>
                        </div>
                        <button class="btn btn-success" type="submit">Add Details</button>
                    </form><!-- Add other form fields as needed -->
                    <button class="btn btn-success loginbtn" type="submit">Add Details</button>
                </form>
            </div>
        </div>

        <div class="col-md-4">
            <div class="card">
                <h1>View All Companies</h1>
                <button class="btn btn-info loginbtn">Get All Company</button>
                <table class="table table-striped">
                    <thead>
                    <tr>
                        <th>COMPANY CODE</th>
                        <th>COMPANY NAME</th>
                        <th>CEO</th>
                        <th>TURNOVER</th>
                        <th>WEBSITE</th>
                        <th>STOCK EXCHANGE</th>
                        <th>STOCK PRICE</th>
                        <th>ACTION</th>
                    </tr>
                    </thead>
                    <tbody>
                    <tr>
                        <td>Aniket</td>
                        <!-- <td>{{ c.companyName }}</td>
                        <td>{{ c.companyCEO }}</td>
                        <td>{{ c.companyTurnover}}</td>
                        <td>{{ c.companyWebsite }}</td>
                        <td>{{ c.stockExchange }}</td>
                        <td>{{ c.stockPrice }}</td> -->

                        <td>
                            <button class="btn btn-warning" >Update</button>
                            <button class="btn btn-danger" >Delete</button>
                        </td>
                    </tr>
                    </tbody>
                </table>
            </div>
        </div>

        <div class="col-md-4">
            <div class="card">
                <h1>Search Company</h1>
                <div id="searchBox">
                    <input type="number">
                    <button class="btn btn-warning loginbtn">Search</button>
                    <table class="table table-striped">
                    <thead>
                    <tr>
                        <th>COMPANY CODE</th>
                        <th>COMPANY NAME</th>
                        <th>CEO</th>
                        <th>COMPANY TURNOVER</th>
                        <th>COMPANY WEBSITE</th>
                        <th>STOCK EXCHANGE</th>
                        <th>STOCK PRICE</th>
                    </tr>
                    </thead>
                    <tbody>
                    <tr >
                        <td>Anand</td>
                        <!-- <td>{{ r.companyName }}</td>
                        <td>{{ r.companyCEO }}</td>
                        <td>{{ r.turnover}}</td>
                        <td>{{ r.website }}</td>
                        <td>{{ r.stockExchange }}</td>
                        <td>{{ r.stockPrice }}</td> -->
                    </tr>
                    </tbody>
                </table>
                </div>
            </div>
        </div>
    </div>
</div>

</body>
</html>
